import{_ as t,o as l,c as a,V as e}from"./chunks/framework.6038b03e.js";const s="/vitepress/assets/ddl.06960eec.png",o="/vitepress/assets/DQL执行顺序.f0f9efc6.png",n="/vitepress/assets/DQL语句.ac53016e.png",p="/vitepress/assets/DCL-管理用户.50ca8e1c.png",r="/vitepress/assets/DCL-权限控制.987c310b.png",i="/vitepress/assets/DCL-总结.a71b83a1.png",C=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"Mysql/SQL语句.md","lastUpdated":null}'),c={name:"Mysql/SQL语句.md"},d=e('<h2 id="sql通用语法" tabindex="-1">SQL通用语法 <a class="header-anchor" href="#sql通用语法" aria-label="Permalink to &quot;SQL通用语法&quot;">​</a></h2><ol><li>SQL语法可以单行或多行书写，以分好结尾。</li><li>SQL语句可以使用空格/缩进来增强到语句的可读性。</li><li>MySQL数据库的SQL语句不区分大小写，关键字建议使用大写。</li><li>注释： <ul><li>单行注释：--注释内容 或 # 注释内容（MySQL特有）</li><li>多行注释：/<em>注释内容</em>/</li></ul></li></ol><h2 id="sql分类" tabindex="-1">SQL分类 <a class="header-anchor" href="#sql分类" aria-label="Permalink to &quot;SQL分类&quot;">​</a></h2><table><thead><tr><th>分类</th><th>全称</th><th>说明</th></tr></thead><tbody><tr><td>DDL</td><td>Date Definition Language</td><td>数据定义语言，用来定义数据库对象（数据库，表，字段）</td></tr><tr><td>DML</td><td>Date Manipulation Language</td><td>数据操作语言，用来对数据库表中的数据进行增删改</td></tr><tr><td>DQL</td><td>Data Query Language</td><td>数据查询语言，用来查询数据库中表的记录</td></tr><tr><td>DCL</td><td>Data Control Language</td><td>数据控制语言，用来创建数据库用户、控制数据库的访问权限</td></tr></tbody></table><h2 id="ddl" tabindex="-1">DDL <a class="header-anchor" href="#ddl" aria-label="Permalink to &quot;DDL&quot;">​</a></h2><h3 id="ddl-数据库操作" tabindex="-1">DDL-数据库操作 <a class="header-anchor" href="#ddl-数据库操作" aria-label="Permalink to &quot;DDL-数据库操作&quot;">​</a></h3><ul><li><p>查询</p><p>查询所有数据库</p><blockquote><p>show databases;</p></blockquote></li><li><p>查询当前数据库</p><blockquote><p>select database();</p></blockquote></li><li><p>创建</p><blockquote><p>create database [if not exists] 数据库名 [default charset 字符集] [collate 排序规则];</p></blockquote></li><li><p>删除</p><blockquote><p>drop databases [if exists] 数据库名;</p></blockquote></li><li><p>使用</p><blockquote><p>use 数据名;</p></blockquote></li></ul><h3 id="ddl-表查询-查询" tabindex="-1">DDL-表查询-查询 <a class="header-anchor" href="#ddl-表查询-查询" aria-label="Permalink to &quot;DDL-表查询-查询&quot;">​</a></h3><ul><li><p>查询当前数据库所有表</p><blockquote><p>show tables;</p></blockquote></li><li><p>查询表结构</p><blockquote><p>desc 表名;</p></blockquote></li><li><p>查询指定表的建表语句</p><blockquote><p>show create table 表明;</p></blockquote></li></ul><h3 id="ddl-表操作-创建" tabindex="-1">DDL-表操作-创建 <a class="header-anchor" href="#ddl-表操作-创建" aria-label="Permalink to &quot;DDL-表操作-创建&quot;">​</a></h3><p><img src="'+s+`" alt=""></p><div class="language-java"><button title="Copy Code" class="copy"></button><span class="lang">java</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">create table </span><span style="color:#82AAFF;">emp</span><span style="color:#89DDFF;">(</span></span>
<span class="line"><span style="color:#A6ACCD;">    id </span><span style="color:#C792EA;">int</span><span style="color:#A6ACCD;"> comment </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">编号</span><span style="color:#89DDFF;">&#39;</span><span style="color:#89DDFF;">,</span></span>
<span class="line"><span style="color:#A6ACCD;">    workno </span><span style="color:#82AAFF;">varchar</span><span style="color:#89DDFF;">(</span><span style="color:#F78C6C;">10</span><span style="color:#89DDFF;">)</span><span style="color:#A6ACCD;"> comment </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">工号</span><span style="color:#89DDFF;">&#39;</span><span style="color:#89DDFF;">,</span></span>
<span class="line"><span style="color:#A6ACCD;">    name </span><span style="color:#82AAFF;">varchar</span><span style="color:#89DDFF;">(</span><span style="color:#F78C6C;">10</span><span style="color:#89DDFF;">)</span><span style="color:#A6ACCD;"> comment </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">姓名</span><span style="color:#89DDFF;">&#39;</span><span style="color:#89DDFF;">,</span></span>
<span class="line"><span style="color:#A6ACCD;">    age tinyint unsigned comment </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">年龄</span><span style="color:#89DDFF;">&#39;</span><span style="color:#89DDFF;">,</span></span>
<span class="line"><span style="color:#A6ACCD;">    idcard </span><span style="color:#82AAFF;">char</span><span style="color:#89DDFF;">(</span><span style="color:#F78C6C;">18</span><span style="color:#89DDFF;">)</span><span style="color:#A6ACCD;"> comment </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">身份证号</span><span style="color:#89DDFF;">&#39;</span><span style="color:#89DDFF;">,</span></span>
<span class="line"><span style="color:#A6ACCD;">    entrydate date comment </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">入职时间</span><span style="color:#89DDFF;">&#39;</span></span>
<span class="line"><span style="color:#89DDFF;">)</span><span style="color:#A6ACCD;"> comment </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">员工表</span><span style="color:#89DDFF;">&#39;</span><span style="color:#89DDFF;">;</span></span></code></pre></div><h3 id="ddl-表操作-修改" tabindex="-1">DDL-表操作-修改 <a class="header-anchor" href="#ddl-表操作-修改" aria-label="Permalink to &quot;DDL-表操作-修改&quot;">​</a></h3><ul><li><p>添加字段</p><blockquote><p>alter table 表名 add 字段名 类型(长度) [comment 注释] [约束];</p></blockquote></li><li><p>修改数据类型</p><blockquote><p>alter table 表名 modlfy 字段名 新数据类型(长度);</p></blockquote></li><li><p>修改字段名和字段类型</p><blockquote><p>alter table 表名 change 旧字段名 新字段名 类型(长度) [comment 注释] [约束];</p></blockquote></li><li><p>删除字段</p><blockquote><p>alter table 表名 drop 字段名;</p></blockquote></li><li><p>修改表名</p><blockquote><p>alter table 表名 rename to 新表名;</p></blockquote></li></ul><h3 id="ddl-表操作-删除" tabindex="-1">DDL-表操作-删除 <a class="header-anchor" href="#ddl-表操作-删除" aria-label="Permalink to &quot;DDL-表操作-删除&quot;">​</a></h3><ul><li><p>删除表</p><blockquote><p>drop table [if exists] 表名;</p></blockquote></li><li><p>删除指定表，并重新创建该表</p><blockquote><p>truncate table 表名;</p></blockquote></li></ul><p><strong><span style="color:red;">注意：在删除表时，表中的全部数据也会被删除！</span></strong></p><h2 id="dml" tabindex="-1">DML <a class="header-anchor" href="#dml" aria-label="Permalink to &quot;DML&quot;">​</a></h2><p>DML英文全称是Data Manipulation Language（数据操作语言），用来对数据库中表的数据记录进行增删改操作。</p><h3 id="dml-添加数据" tabindex="-1">DML-添加数据 <a class="header-anchor" href="#dml-添加数据" aria-label="Permalink to &quot;DML-添加数据&quot;">​</a></h3><ol><li><p>给指定字段添加数据</p><blockquote><p>insert into 表名(字段名1，字段名2，......) values (值1, 值2, ...);</p></blockquote></li><li><p>给全部字段添加数据</p><blockquote><p>insert into 表名 values (值1， 值2, ......);</p></blockquote></li><li><p>批量添加数据</p><blockquote><p>insert into 表名(字段名1,字段名2,......) values (值1, 值2, ......),(值1, 值2, ......),(值1, 值2, ......);</p></blockquote><blockquote><p>insert into 表名 values (值1, 值2, ......),(值1, 值2, ......),(值1, 值2, ......);</p></blockquote><div class="language-java"><button title="Copy Code" class="copy"></button><span class="lang">java</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">注意：</span></span>
<span class="line"><span style="color:#A6ACCD;">    插入数据时，指定的字段顺序需要与值的顺序是一一对应的。</span></span>
<span class="line"><span style="color:#A6ACCD;">    字符串和日期型数据应该包含在引导中。</span></span>
<span class="line"><span style="color:#A6ACCD;">    插入的数据大小，应该在字段的规定范围内。</span></span></code></pre></div></li></ol><h3 id="dml-修改数据" tabindex="-1">DML-修改数据 <a class="header-anchor" href="#dml-修改数据" aria-label="Permalink to &quot;DML-修改数据&quot;">​</a></h3><blockquote><p>update 表名 set 字段名1=值1, 字段名2=值2, ...... [where 条件];</p></blockquote><p><strong><span style="color:red;">注意：修改语句的条件可以有，也可以没有，如果没有条件，则会修改整张表的所有数据。</span></strong></p><h3 id="dml-删除数据" tabindex="-1">DML-删除数据 <a class="header-anchor" href="#dml-删除数据" aria-label="Permalink to &quot;DML-删除数据&quot;">​</a></h3><blockquote><p>delete from 表名 [where 条件];</p></blockquote><div class="language-java"><button title="Copy Code" class="copy"></button><span class="lang">java</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">注意：</span></span>
<span class="line"><span style="color:#A6ACCD;">    delete语句的条件可以有，也可以没有，如果没有条件，则会删除整张表的所有数据。</span></span>
<span class="line"><span style="color:#A6ACCD;">    delete语句不能删除某一个字段的值（可以使用update）。</span></span></code></pre></div><h2 id="dql" tabindex="-1">DQL <a class="header-anchor" href="#dql" aria-label="Permalink to &quot;DQL&quot;">​</a></h2><p>DQL英文全称是Data Query Language（数据查询语句），数据查询语句，用来查询数据库中表的记录。</p><ol><li><p>查询多个字段</p><blockquote><p>select 字段1, 字段2, 字段3 ...... from 表名;</p></blockquote><blockquote><p>select * from 表名;</p></blockquote></li><li><p>设置别名</p><blockquote><p>select 字段1 [as 别名1], 字段2 [as 别名2], ...... from 表名;</p></blockquote></li><li><p>去除重复记录</p><blockquote><p>select distinct 字段列表 from 表名;</p></blockquote></li></ol><h3 id="dql-条件查询" tabindex="-1">DQL-条件查询 <a class="header-anchor" href="#dql-条件查询" aria-label="Permalink to &quot;DQL-条件查询&quot;">​</a></h3><ol><li><p>语法</p><blockquote><p>select 字段列表 from 表名 where 条件列表;</p></blockquote></li></ol><table><thead><tr><th style="text-align:left;">比较运算符</th><th style="text-align:left;">功能</th></tr></thead><tbody><tr><td style="text-align:left;">&gt;</td><td style="text-align:left;">大于</td></tr><tr><td style="text-align:left;">&gt;=</td><td style="text-align:left;">大于等于</td></tr><tr><td style="text-align:left;">&lt;</td><td style="text-align:left;">小于</td></tr><tr><td style="text-align:left;">&lt;=</td><td style="text-align:left;">小于等于</td></tr><tr><td style="text-align:left;">=</td><td style="text-align:left;">等于</td></tr><tr><td style="text-align:left;">&lt;&gt; 或 !=</td><td style="text-align:left;">不等于</td></tr><tr><td style="text-align:left;">BETWEEN ... AND...</td><td style="text-align:left;">在某个范围之内（含最小、最大值）</td></tr><tr><td style="text-align:left;">IN(...)</td><td style="text-align:left;">在in之后的列表中的值，多选一</td></tr><tr><td style="text-align:left;">LIKE 占位符</td><td style="text-align:left;">模糊匹配（_匹配单个字符，%匹配任意个字符）</td></tr><tr><td style="text-align:left;">IS NULL</td><td style="text-align:left;">是NULL</td></tr></tbody></table><table><thead><tr><th>逻辑运算符</th><th>功能</th></tr></thead><tbody><tr><td>AND 或 &amp;&amp;</td><td>并且（多个条件同时成立）</td></tr><tr><td>OR 或 ||</td><td>或者（多个条件任意一个成立）</td></tr><tr><td>NOT 或 !</td><td>非，不是</td></tr></tbody></table><h3 id="dql-聚合函数" tabindex="-1">DQL-聚合函数 <a class="header-anchor" href="#dql-聚合函数" aria-label="Permalink to &quot;DQL-聚合函数&quot;">​</a></h3><ol><li><p>介绍</p><p>将一列数据作为一个整体，进行纵向计算。</p></li><li><p>常见聚合函数</p><table><thead><tr><th><span style="color:red;background-color:yellow;">函数</span></th><th><span style="color:red;background-color:yellow;">功能</span></th></tr></thead><tbody><tr><td>count</td><td>统计数量</td></tr><tr><td>max</td><td>最大值</td></tr><tr><td>min</td><td>最小值</td></tr><tr><td>avg</td><td>平均值</td></tr><tr><td>sum</td><td>求和</td></tr></tbody></table></li><li><p>语法</p><blockquote><p>select 聚合函数(字段列表) from 表名;</p></blockquote></li></ol><p><strong><span style="color:red;">注意：null不参与所有聚合函数运算。</span></strong></p><h3 id="dql-分组查询" tabindex="-1">DQL-分组查询 <a class="header-anchor" href="#dql-分组查询" aria-label="Permalink to &quot;DQL-分组查询&quot;">​</a></h3><ol><li><p>语法</p><blockquote><p>select 字段列表 from 表名 [where 条件] group by 分组字段名 [having 分组后过滤条件];</p></blockquote></li><li><p>where与having区别</p><ul><li>执行时机不同：where是分组之前进行过滤，不满足where条件，不参与分组；而having是分组之后对结果进行过滤。</li><li>判断条件不同：where不能对聚合函数进行判断，而having可以。</li></ul></li></ol><h3 id="dql-排序查询" tabindex="-1">DQL-排序查询 <a class="header-anchor" href="#dql-排序查询" aria-label="Permalink to &quot;DQL-排序查询&quot;">​</a></h3><ol><li><p>语法</p><blockquote><p>select 字段列表 from 表名 order by 字段1 排序方式1，字段2 排序方式2;</p></blockquote></li><li><p>排序方式</p><ul><li>ASC：升序（默认值）</li><li>DESC：降序</li></ul></li></ol><p><strong><span style="color:red;">注意：如果是多个字段排序，当第一个字段值相同时，才会根据第二个字段进行排序。</span></strong></p><h3 id="dql-分页查询" tabindex="-1">DQL-分页查询 <a class="header-anchor" href="#dql-分页查询" aria-label="Permalink to &quot;DQL-分页查询&quot;">​</a></h3><ol><li><p>语法</p><blockquote><p>select 字段列表 from 表名 limit 起始索引，查询记录数;</p></blockquote></li></ol><div class="language-my"><button title="Copy Code" class="copy"></button><span class="lang">my</span><pre class="shiki material-theme-palenight"><code><span class="line"><span style="color:#A6ACCD;">注意：</span></span>
<span class="line"><span style="color:#A6ACCD;">  起始索引从0开始，起始索引=（查询页码 - 1）* 每页显示记录数。</span></span>
<span class="line"><span style="color:#A6ACCD;">  分页查询时数据库的方言，不同的数据库有不同的实现，MySQL中是limit。</span></span>
<span class="line"><span style="color:#A6ACCD;">  如果查询的是第一个数据，起始索引可以省略，直接简写为limit 10。</span></span></code></pre></div><h3 id="总结" tabindex="-1">总结 <a class="header-anchor" href="#总结" aria-label="Permalink to &quot;总结&quot;">​</a></h3><p><img src="`+o+'" alt=""></p><p><img src="'+n+'" alt=""></p><h2 id="dcl" tabindex="-1">DCL <a class="header-anchor" href="#dcl" aria-label="Permalink to &quot;DCL&quot;">​</a></h2><p><img src="'+p+'" alt=""></p><p><img src="'+r+'" alt=""></p><h3 id="总结-1" tabindex="-1">总结 <a class="header-anchor" href="#总结-1" aria-label="Permalink to &quot;总结&quot;">​</a></h3><p><img src="'+i+'" alt=""></p>',53),h=[d];function u(D,b,y,q,m,k){return l(),a("div",null,h)}const f=t(c,[["render",u]]);export{C as __pageData,f as default};
